import React, { useState } from "react";

const InputForm = () => {
  const [formData, setFormData] = useState({
    "current-savings": 0,
    "yearly-contribution": 0,
    expInterest: 0,
    invDuration: 0,
  });

  const resetHandler = () => {
    console.log("resetted");
  };

  const changeHandler = (input, value) => {
    console.log(input, value);
  };

  const calculateHandler = () => {
    console.log("calculated");
  };

  const submitHandler = (event) => {
    event.preventDefault();
    console.log("submitted");
  };

  return (
    <form className="form" onSubmit={submitHandler}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            value={formData.currSavings}
            type="number"
            id="current-savings"
            onChange={(event) =>
              changeHandler("current-savings", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            value={formData.yrlySavings}
            type="number"
            id="yearly-contribution"
            onChange={(event) =>
              changeHandler("yearly-contribution", event.target.value)
            }
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            value={formData.expInterest}
            type="number"
            id="expected-return"
            onChange={(event) =>
              changeHandler("expected-return", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            value={formData.invDuration}
            type="number"
            id="duration"
            onChange={(event) => changeHandler("duration", event.target.value)}
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt" onClick={resetHandler}>
          Reset
        </button>
        <button type="submit" className="button" onClick={calculateHandler}>
          Calculate
        </button>
      </p>
    </form>
  );
};

export default InputForm;
